import { Component, HostListener, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-intro',
  templateUrl: './intro.component.html',
  styleUrls: ['./intro.component.scss']
})
export class IntroComponent implements OnInit {
  title: string = "Ruwenzori Farms";
  activeSection: string | null = null;
  isMenuOpen: boolean = false;  // Track the state of the menu

  constructor(
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit() {
    this.route.fragment.subscribe(fragment => {
      this.activeSection = fragment;
    });
  }

  scrollToSection(section: string) {
    this.router.navigate([], { fragment: section, replaceUrl: true });

    const element = document.getElementById(section);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    this.isMenuOpen = false; 
  }

  goToLogin() {
    this.router.navigate(['login']);
  }

  @HostListener('window:scroll', [])
  onWindowScroll() {
    const sections = ['home', 'about', 'services', 'contact'];
    for (let section of sections) {
      const element = document.getElementById(section);
      if (element) {
        const rect = element.getBoundingClientRect();
        if (rect.top >= 0 && rect.top <= window.innerHeight / 2) {
          this.activeSection = section;
          break;
        }
      }
    }
  }

  toggleMenu() {
    this.isMenuOpen = !this.isMenuOpen;
  }
}
<div class="bg-white h-screen">
  <nav appScrollSpy class="bg-white border-gray-200 dark:bg-gray-900">
    <div class="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
      <a href="#home" class="flex items-center space-x-3 rtl:space-x-reverse">
        <img src="../../../assets/ruwenzorilogo1.png" class="h-10" alt="Logo" />
        <span class="self-center text-2xl font-semibold whitespace-nowrap dark:text-white m-2">{{ title }}</span>
      </a>
      <div class="flex md:order-2 space-x-3 md:space-x-0 rtl:space-x-reverse">
        <button type="button" class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800" (click)="goToLogin()">Get started</button>
        <button data-collapse-toggle="navbar-cta" type="button" class="inline-flex items-center p-2 w-10 h-10 justify-center text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200 dark:text-gray-400 dark:hover:bg-gray-700 dark:focus:ring-gray-600" aria-controls="navbar-cta" aria-expanded="false" (click)="toggleMenu()">
          <span class="sr-only">Open main menu</span>
          <svg class="w-5 h-5" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 17 14">
            <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 1h15M1 7h15M1 13h15" />
          </svg>
        </button>
      </div>
      <div class="items-center justify-between w-full md:flex md:w-auto md:order-1" [class.hidden]="!isMenuOpen">
        <ul class="flex flex-col font-medium p-4 md:p-0 mt-4 border border-gray-100 rounded-lg bg-gray-50 md:space-x-8 rtl:space-x-reverse md:flex-row md:mt-0 md:border-0 md:bg-white dark:bg-gray-800 md:dark:bg-gray-900 dark:border-gray-700">
          <li>
            <a href="#home" (click)="scrollToSection('home')" [class.active]="activeSection === 'home'">Home</a>
          </li>
          <li>
            <a href="#about" (click)="scrollToSection('about')" [class.active]="activeSection === 'about'">About</a>
          </li>
          <li>
            <a href="#services" (click)="scrollToSection('services')" [class.active]="activeSection === 'services'">Products</a>
          </li>
          <li>
            <a href="#contact" (click)="scrollToSection('contact')" [class.active]="activeSection === 'contact'">Contact</a>
          </li>
        </ul>
      </div>
    </div>
  </nav>

  <!-- Home Section -->
  <section id="home" class="main_container w-full md:w-full">
      <app-home></app-home>
  </section>

  <!-- About Section -->
  <section id="about" class="bg-white w-full md:w-full mx-auto p-4">
    <app-about></app-about>
  </section>

  <!-- Services Section -->
  <section id="services" class="bg-white w-full md:w-full mx-auto p-4">
    <app-services></app-services>
  </section>

  <!-- Contact Section -->
  <section id="contact" class="bg-white w-full md:w-full mx-auto p-4">
    <app-contact></app-contact>
  </section>

  <!-- Footer Section -->
  <section id="" class="bg-white w-full md:w-full mx-auto p-4">
    <app-footer></app-footer>
  </section>
</div>
